mapper.insert:
 INSERT INTO EL_USER
 ( 
   APP_ID           
  , USER_ID           
  , USER_NM             
  , USER_GROUP_NO       
  , USED_YN              
 )
 VALUES  
 ( 
  #{appId}   
,  #{userId}   
  , #{userNm}          
  , #{userGroupNo} 
  , #{usedYn}                        
 )

mapper.update:
UPDATE EL_USER
, USER_NM = #{userNm} , USER_GROUP_NO = #{userGroupNo} , USED_YN = #{usedYn}
WHERE USER_ID = #{userId} AND APP_ID = #{appId}
